mapper.select:
select

from menu_evaluate
where menu_evaluate_id = #{id,jdbcType=BIGINT} and is_delete=0 and is_root_reply=1  limit 1;

select

from menu_evaluate
where  is_delete=0

  and  menu_id = #{menuId,jdbcType=BIGINT}


  and user_id = #{userId,jdbcType=BIGINT}


  and menu_evaluate  LIKE concat( '%',#{evaluateName},'%')

  and is_root_reply=0 and menu_type=1;

select
from menu_evaluate where is_delete=0 and is_root_reply=0 and menu_type=1 and menu_id in
#{item}

select
from menu_evaluate where is_delete=0 and is_root_reply=1 and menu_type=2 and menu_evaluate_id in
#{item}

select
m1.*
from menu_evaluate AS m1 LEFT JOIN menu_ae as m2 ON m1.menu_id=m2.id
where  m1.is_delete=0 and m2.is_delete=0

  and  m1.menu_id = #{menuId,jdbcType=BIGINT}


  and m1.user_id = #{userId,jdbcType=BIGINT}


  and m2.menu_name  LIKE concat( '%',#{evaluateName},'%')

and m1.is_root_reply=0 and m1.menu_type=1;
